import React from 'react'
import {Field, reduxForm} from 'redux-form'
import {Form} from 'react-bootstrap';
import Button from 'material-ui/Button';
import {withRouter} from 'react-router'
import {FieldGroup} from 'helpers/ui'
// import validate from '../../helpers/validate';

let PostForm = (props) => {
  // debugger
  const {handleSubmit, isCreate, updatePost} = props;
  const submit = (values) => {
    // debugger
    updatePost(values)//.then((res) => props.history.push('/'))
  }
  //const title = (props.isNew ? 'Create' : 'Edit') + ' User'
  const title = 'form edit'
  return (
    <div>
      <h1>{title}</h1>
      <Form onSubmit={handleSubmit(submit)} className="edit_company_user">
        <Field
          label='Title'
          name="title"
          component='input'
          type="text"
          placeholder="Please input title"
        />
        <Field
          label='Text'
          name="text"
          component='input'
          type="textarea"
          placeholder="Please input text"
        />
        <Button type="submit">{title}</Button>
      </Form>
    </div>
  )
}

PostForm = reduxForm({
  form: 'form_post'
})(PostForm);

export default withRouter(PostForm)