import R from 'ramda'
import actionTypes from 'actionTypes'

const {
  FETCH_POSTS_START,
  FETCH_POSTS_SUCCESS,
  FETCH_POSTS_FAILURE,
} = actionTypes

const initialState = []

export default (state = initialState, {type, payload}) => {
  switch (type) {
    case FETCH_POSTS_START:
      return R.merge(state, payload)
    case FETCH_POSTS_SUCCESS:
      return payload.data
    case FETCH_POSTS_FAILURE:
      return R.merge(state, payload)
    default:
      return state
  }
}